CREATE ALGORITHM=UNDEFINED DEFINER=`readingbar`@`%` SQL SECURITY DEFINER  VIEW `v_messages` AS select `msg`.`id` AS `id`,`msg`.`reply` AS `reply`,`msg`.`sendfrom` AS `sendfrom`,`msg`.`sendto` AS `sendto`,ifnull(`m1`.`email`,`u1`.`email`) AS `sendfrom_email`,ifnull(`m1`.`cellphone`,`u1`.`cellphone`) AS `sendfrom_cellphone`,ifnull(`u2`.`email`,`m2`.`email`) AS `sendto_email`,ifnull(`u2`.`cellphone`,`m2`.`cellphone`) AS `sendto_cellphone`,`msg`.`title` AS `title`,`msg`.`content` AS `content`,`msg`.`message_type` AS `message_type`,`msg`.`created_at` AS `created_at`,`msg`.`updated_at` AS `updated_at` from ((((`messages` `msg` left join `users` `u1` on((`msg`.`sendfrom` = `u1`.`id`))) left join `users` `u2` on((`msg`.`sendto` = `u2`.`id`))) left join `members` `m1` on((`msg`.`sendfrom` = `m1`.`id`))) left join `members` `m2` on((`msg`.`sendto` = `m2`.`id`))) ;

